import React, { useEffect, useRef } from 'react';
import { animeAnimations } from '../utils/animations';

const AnimatedBackground = () => {
  const particlesRef = useRef(null);

  useEffect(() => {
    // Create floating particles
    const createParticles = () => {
      const container = particlesRef.current;
      if (!container) return;

      // Clear existing particles
      container.innerHTML = '';

      // Create 20 particles
      for (let i = 0; i < 20; i++) {
        const particle = document.createElement('div');
        particle.className = 'particle';
        particle.style.cssText = `
          position: absolute;
          width: ${Math.random() * 6 + 2}px;
          height: ${Math.random() * 6 + 2}px;
          background: rgba(255, 255, 255, ${Math.random() * 0.8 + 0.2});
          border-radius: 50%;
          left: ${Math.random() * 100}%;
          top: ${Math.random() * 100}%;
          pointer-events: none;
        `;
        container.appendChild(particle);
      }

      // Animate particles
      animeAnimations.particles('.particle');
    };

    // Create particles after component mounts
    setTimeout(createParticles, 100);

    // Recreate particles every 10 seconds for variety
    const interval = setInterval(createParticles, 10000);

    return () => clearInterval(interval);
  }, []);

  const styles = {
    background: {
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      zIndex: -1,
      overflow: 'hidden',
    },
    particles: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
    },
    gradientOverlay: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      background: `
        radial-gradient(circle at 20% 80%, rgba(120, 119, 198, 0.3) 0%, transparent 50%),
        radial-gradient(circle at 80% 20%, rgba(255, 119, 198, 0.3) 0%, transparent 50%),
        radial-gradient(circle at 40% 40%, rgba(120, 219, 255, 0.2) 0%, transparent 50%)
      `,
    },
    mesh: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      backgroundImage: `
        radial-gradient(circle at 1px 1px, rgba(255,255,255,0.15) 1px, transparent 0)
      `,
      backgroundSize: '50px 50px',
      animation: 'meshMove 20s linear infinite',
    }
  };

  return (
    <div style={styles.background}>
      <div style={styles.gradientOverlay}></div>
      <div style={styles.mesh}></div>
      <div ref={particlesRef} style={styles.particles}></div>
      <style>{`
        @keyframes meshMove {
          0% { transform: translate(0, 0); }
          100% { transform: translate(50px, 50px); }
        }
      `}</style>
    </div>
  );
};

export default AnimatedBackground;
