import React, { useState, useEffect, useRef } from 'react';
import { gsapAnimations, animeAnimations, animationPresets } from '../utils/animations';

const TaskItem = ({ task, onToggle, onDelete, onEdit, isAdmin }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(task.title);
  const taskRef = useRef(null);
  const buttonRefs = useRef([]);

  // Animation on mount
  useEffect(() => {
    if (taskRef.current) {
      gsapAnimations.taskEnter(taskRef.current);
    }
  }, []);

  // Update editTitle when task.title changes (after successful edit)
  useEffect(() => {
    setEditTitle(task.title);
  }, [task.title]);

  const handleEditSave = () => {
    if (editTitle.trim() && editTitle !== task.title) {
      onEdit(task._id, editTitle.trim());
    }
    setIsEditing(false);
    // Don't reset editTitle here - let useEffect handle it when task.title updates
  };

  const handleEditCancel = () => {
    setIsEditing(false);
    setEditTitle(task.title); // Reset to original title
  };

  const handleToggle = () => {
    if (taskRef.current) {
      // Celebration animation for task completion
      if (!task.completed) {
        animationPresets.taskComplete(taskRef.current);
      }
    }
    onToggle(task._id);
  };

  const handleDelete = () => {
    if (taskRef.current) {
      gsapAnimations.taskExit(taskRef.current, () => {
        onDelete(task._id);
      });
    } else {
      onDelete(task._id);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleEditSave();
    } else if (e.key === 'Escape') {
      handleEditCancel();
    }
  };

  // Button hover animations
  const handleButtonHover = (index) => {
    if (buttonRefs.current[index]) {
      gsapAnimations.buttonHover(buttonRefs.current[index]);
    }
  };

  const handleButtonUnhover = (index) => {
    if (buttonRefs.current[index]) {
      gsapAnimations.buttonUnhover(buttonRefs.current[index]);
    }
  };

  return (
    <div 
      ref={taskRef}
      style={{...styles.taskItem, ...(task.completed ? styles.completedTask : {})}}
      className="task-item-animated"
    >
      <div style={styles.taskContent}>
        {isEditing ? (
          <input
            type="text"
            value={editTitle}
            onChange={(e) => setEditTitle(e.target.value)}
            onKeyDown={handleKeyPress}
            onBlur={handleEditSave}
            style={styles.editInput}
            autoFocus
          />
        ) : (
          <span
            onClick={!isEditing ? handleToggle : undefined}
            style={{
              ...styles.taskTitle,
              textDecoration: task.completed ? 'line-through' : 'none',
              color: task.completed ? '#6c757d' : '#333',
              cursor: !isEditing ? 'pointer' : 'default'
            }}
          >
            {task.title}
          </span>
        )}
        
        {task.completed && !isEditing && <span style={styles.completedBadge}>âœ“ Completed</span>}
      </div>
      
      <div style={styles.taskActions}>
        {isEditing ? (
          <>
            <button 
              onClick={handleEditSave}
              style={{...styles.actionButton, ...styles.saveButton}}
            >
              Save
            </button>
            <button 
              onClick={handleEditCancel}
              style={{...styles.actionButton, ...styles.cancelButton}}
            >
              Cancel
            </button>
          </>
        ) : (
          <>
            <button 
              onClick={onToggle}
              style={{
                ...styles.actionButton,
                ...(task.completed ? styles.undoButton : styles.completeButton)
              }}
            >
              {task.completed ? 'Undo' : 'Complete'}
            </button>
            
            <button 
              onClick={() => setIsEditing(true)}
              style={{...styles.actionButton, ...styles.editButton}}
            >
              Edit
            </button>
            
            {/* Show delete button for admin users or the task owner */}
            <button 
              onClick={onDelete}
              style={{...styles.actionButton, ...styles.deleteButton}}
            >
              Delete
            </button>
          </>
        )}
      </div>
    </div>
  );
};

const styles = {
  taskItem: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1.5rem',
    backgroundColor: 'rgba(255, 255, 255, 0.95)',
    border: 'none',
    borderRadius: '16px',
    transition: 'all 0.3s ease',
    boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)',
    backdropFilter: 'blur(10px)',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    '&:hover': {
      transform: 'translateY(-2px)',
      boxShadow: '0 8px 30px rgba(0, 0, 0, 0.12)',
    },
  },
  completedTask: {
    backgroundColor: 'rgba(248, 250, 252, 0.9)',
    opacity: 0.7,
  },
  taskContent: {
    display: 'flex',
    alignItems: 'center',
    gap: '1rem',
    flex: 1,
  },
  taskTitle: {
    fontSize: '1.1rem',
    cursor: 'pointer',
    userSelect: 'none',
    fontWeight: '500',
    color: '#2d3748',
    transition: 'color 0.2s ease',
  },
  editInput: {
    fontSize: '1.1rem',
    padding: '0.75rem',
    border: '2px solid #667eea',
    borderRadius: '12px',
    outline: 'none',
    flex: 1,
    backgroundColor: '#fff',
    fontWeight: '500',
    boxShadow: '0 0 0 3px rgba(102, 126, 234, 0.1)',
  },
  completedBadge: {
    backgroundColor: '#48bb78',
    color: 'white',
    padding: '0.4rem 0.8rem',
    borderRadius: '20px',
    fontSize: '0.8rem',
    fontWeight: '600',
    boxShadow: '0 2px 8px rgba(72, 187, 120, 0.3)',
  },
  taskActions: {
    display: 'flex',
    gap: '0.75rem',
  },
  actionButton: {
    padding: '0.6rem 1.2rem',
    border: 'none',
    borderRadius: '10px',
    cursor: 'pointer',
    fontSize: '0.9rem',
    fontWeight: '600',
    transition: 'all 0.3s ease',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
    '&:hover': {
      transform: 'translateY(-1px)',
      boxShadow: '0 4px 15px rgba(0, 0, 0, 0.15)',
    },
  },
  completeButton: {
    backgroundColor: '#48bb78',
    color: 'white',
    '&:hover': {
      backgroundColor: '#38a169',
    },
  },
  undoButton: {
    backgroundColor: '#ed8936',
    color: 'white',
    '&:hover': {
      backgroundColor: '#dd6b20',
    },
  },
  editButton: {
    backgroundColor: '#667eea',
    color: 'white',
    '&:hover': {
      backgroundColor: '#5a67d8',
    },
  },
  saveButton: {
    backgroundColor: '#48bb78',
    color: 'white',
    '&:hover': {
      backgroundColor: '#38a169',
    },
  },
  cancelButton: {
    backgroundColor: '#a0aec0',
    color: 'white',
    '&:hover': {
      backgroundColor: '#718096',
    },
  },
  deleteButton: {
    backgroundColor: '#f56565',
    color: 'white',
    '&:hover': {
      backgroundColor: '#e53e3e',
    },
  },
};

export default TaskItem;
