import React, { useEffect, useState, useRef } from 'react';
import { AuthProvider, useAuth } from './context/AuthContext';
import Login from './components/Login';
import Register from './components/Register';
import Header from './components/Header';
import TaskItem from './components/TaskItem';
import AdminPanel from './components/AdminPanel';
import { gsapAnimations, animeAnimations, animationUtils } from './utils/animations';

const API_URL = 'http://localhost:3000/api/tasks';

function TodoApp() {
  const { user, isAdmin } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const containerRef = useRef(null);
  const taskListRef = useRef(null);
  const addButtonRef = useRef(null);

  useEffect(() => {
    if (user) {
      fetchTasks();
    }
  }, [user]);

  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(API_URL, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setTasks(data);
      } else if (response.status === 401) {
        // Token expired or invalid
        const { logout } = useAuth();
        logout();
      }
    } catch (error) {
      setError('Failed to fetch tasks');
    }
  };

  const addTask = async () => {
    if (!title.trim()) return;
    
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(API_URL, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ title })
      });
      
      if (res.ok) {
        const newTask = await res.json();
        setTasks([...tasks, newTask]);
        setTitle('');
      } else {
        setError('Failed to add task');
      }
    } catch (error) {
      setError('Failed to add task');
    } finally {
      setLoading(false);
    }
  };

  const toggleTask = async (id, completed) => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`${API_URL}/${id}`, {
        method: 'PUT',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ completed: !completed })
      });
      
      if (res.ok) {
        const updated = await res.json();
        setTasks(tasks.map(t => t._id === id ? updated : t));
      } else {
        setError('Failed to update task');
      }
    } catch (error) {
      setError('Failed to update task');
    }
  };

  const deleteTask = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`${API_URL}/${id}`, { 
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (res.ok) {
        setTasks(tasks.filter(t => t._id !== id));
      } else {
        setError('Failed to delete task');
      }
    } catch (error) {
      setError('Failed to delete task');
    }
  };

  const editTask = async (id, newTitle) => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`${API_URL}/${id}`, {
        method: 'PUT',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ title: newTitle })
      });
      
      if (res.ok) {
        const updated = await res.json();
        setTasks(tasks.map(t => t._id === id ? updated : t));
      } else {
        setError('Failed to edit task');
      }
    } catch (error) {
      setError('Failed to edit task');
    }
  };

  if (!user) {
    return null; // This will be handled by the main App component
  }

  return (
    <div>
      <Header />
      <div style={styles.container}>
        {error && <div style={styles.error}>{error}</div>}
        
        <div style={styles.addTaskSection}>
          <input 
            value={title} 
            onChange={e => setTitle(e.target.value)}
            placeholder="Enter a new task..."
            style={styles.input}
            onKeyPress={(e) => e.key === 'Enter' && addTask()}
          />
          <button 
            onClick={addTask}
            disabled={loading || !title.trim()}
            style={{...styles.addButton, ...(loading ? styles.addButtonDisabled : {})}}
          >
            {loading ? 'Adding...' : 'Add Task'}
          </button>
        </div>

        {isAdmin() && <AdminPanel />}

        <div style={styles.taskList}>
          {tasks.length === 0 ? (
            <p style={styles.noTasks}>No tasks yet. Add one above!</p>
          ) : (
            tasks.map(t => (
              <TaskItem
                key={t._id}
                task={t}
                onToggle={() => toggleTask(t._id, t.completed)}
                onDelete={() => deleteTask(t._id)}
                onEdit={editTask}
                isAdmin={isAdmin()}
              />
            ))
          )}
        </div>
      </div>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <MainApp />
    </AuthProvider>
  );
}

function MainApp() {
  const { user, login, loading } = useAuth();
  const [showRegister, setShowRegister] = useState(false);

  if (loading) {
    return <div style={styles.loading}>Loading...</div>;
  }

  if (!user) {
    if (showRegister) {
      return (
        <Register 
          onRegister={login} 
          onSwitchToLogin={() => setShowRegister(false)} 
        />
      );
    } else {
      return (
        <Login 
          onLogin={login} 
          onSwitchToRegister={() => setShowRegister(true)} 
        />
      );
    }
  }

  return <TodoApp />;
}

const styles = {
  container: {
    maxWidth: '900px',
    margin: '0 auto',
    padding: '2rem 1rem',
    minHeight: 'calc(100vh - 80px)',
  },
  loading: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    fontSize: '1.5rem',
    color: '#fff',
    fontWeight: '500',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
  },
  error: {
    background: 'linear-gradient(135deg, #ff6b6b, #ee5a52)',
    color: '#fff',
    padding: '1rem 1.5rem',
    borderRadius: '16px',
    marginBottom: '1.5rem',
    boxShadow: '0 8px 25px rgba(255, 107, 107, 0.3)',
    fontSize: '0.95rem',
    fontWeight: '500',
    border: 'none',
    backdropFilter: 'blur(10px)',
  },
  addTaskSection: {
    display: 'flex',
    gap: '1rem',
    marginBottom: '2rem',
    background: 'rgba(255, 255, 255, 0.95)',
    padding: '1.5rem',
    borderRadius: '20px',
    boxShadow: '0 10px 40px rgba(0, 0, 0, 0.1)',
    backdropFilter: 'blur(20px)',
    border: '1px solid rgba(255, 255, 255, 0.3)',
    transition: 'all 0.3s ease',
  },
  input: {
    flex: 1,
    padding: '1rem 1.5rem',
    border: '2px solid rgba(102, 126, 234, 0.1)',
    borderRadius: '14px',
    fontSize: '1rem',
    outline: 'none',
    transition: 'all 0.3s ease',
    backgroundColor: '#fff',
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)',
    fontFamily: 'inherit',
  },
  addButton: {
    padding: '1rem 2.5rem',
    background: 'linear-gradient(135deg, #667eea, #764ba2)',
    color: 'white',
    border: 'none',
    borderRadius: '14px',
    cursor: 'pointer',
    fontSize: '1rem',
    fontWeight: '600',
    transition: 'all 0.3s ease',
    boxShadow: '0 6px 20px rgba(102, 126, 234, 0.3)',
    fontFamily: 'inherit',
    whiteSpace: 'nowrap',
  },
  addButtonDisabled: {
    background: 'linear-gradient(135deg, #a0aec0, #cbd5e0)',
    cursor: 'not-allowed',
    transform: 'none',
    boxShadow: '0 3px 10px rgba(160, 174, 192, 0.2)',
  },
  taskList: {
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem',
  },
  noTasks: {
    textAlign: 'center',
    color: 'rgba(255, 255, 255, 0.9)',
    fontSize: '1.2rem',
    fontWeight: '500',
    padding: '3rem 2rem',
    background: 'rgba(255, 255, 255, 0.15)',
    borderRadius: '20px',
    backdropFilter: 'blur(20px)',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
  },
};

export default App;
